			mSpellDescriptions.add(new SpellDescriptionRecord("Detect Morals II", "This spell is very similar to \"Detect Morals I\" except the caster does not need to specify which of the three types of morals he is trying to detect, AND if he is successful in detecting he will know the EXACT morals of his subject.")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Detect Secret Doors", "This spell gives the casting mage a 50% + 5% x level chance of detecting a secret door when within 5' of the secret area (he must search 10 min. for it), and a 20% chance of detecting one when within 30' of it.", new Pair("Duration", "20 min. + 10 min. x lvl."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Detect Traps", "Just like \"Detect Secret Doors\", but works for traps (wow). This spell also negates the modifiers on the die roll when saving Vs surprise and the party is ambushed (so if a well planned ambush takes place, ignore the modifier).")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Diminution", "This spell will reduce a creature's size to 1/12 that of normal (a save Vs magic +20% is allowed if the subject is unwilling). This spell adds +30% to the subject's Defense when dodging, but reduces their Hits and Stamina (as well as other things, like Movement) to 1/12 that of normal.", new Pair("Range", "30'."), new Pair("Duration", "2 hours x lvl."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Disguise", "This spell allows the casting mage to alter his/her appearance. The mage may even choose to resemble another race of humanoid. Height may be changed by as much as one foot (+l\" x lvl). Weight may be changed by as much as 100 lbs (+5 lbs x lvl). The mage may go bald, have hair, change skin color, sex, voice, etc. He may attempt to disguise himself to appear as some specific person, but all others that know the person being imitated w are allowed a save Vs magic -10% when coming within 10 of the imitator for the first time. If compared face to face with the real one, the spell has a 40% chance (-2% x lvl) of failing immediately. At 8th lvl the mage may cast this spell on another (1/2 duration).", new Pair("Duration", "3 hours x lvl."), new Pair("Range", "touch"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Disintegrate", "Living creatures must save Vs magic or take lOD10 damage (if save made take 5D10). Only one creature is affected. Any non -magical object is destroyed (up to 1000 cu. ft.). Magical objects vary and are left up to the referee (sorry!).", new Pair("Range", "Touch"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
