			mSpellDescriptions.add(new SpellDescriptionRecord("Fire Rune", "This rune is painted with special inks costing 100 sp per rune and 1 day to prepare. It may be painted on any object that opens and closes (like a book, door, etc.). If anyone other than the casting mage opens the object the rune will explode doing lD6+1vl damage to all within 10' radius (No Save). The rune may be spotted", new Pair("by any elementalist in the area of fire (success", "20% x lvl) and may be removed by any elementalist that has the spell (success 10% x lvl +5% per lvl of difference from that of the casting"), new Pair("mage...failure", "\"boom\")."), new Pair("Range", "touch (must be painted on)."), new Pair("Duration", "until removed (a \"Dispell Magic\" won't do it)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$ //$NON-NLS-9$ //$NON-NLS-10$
