			mSpellDescriptions.add(new SpellDescriptionRecord("Fireworks", "This spell creates a flashing display of aerial fireworks. The area", new Pair("of effect", "lvl x lvl x 100 square feet (so an 8th lvl Elementalist casting this spell would have a 6400 sq.ft. area (80'x80'). It may also be used to create a smoke cloud on the ground with the same"), new Pair("dimensions (and 10' to 30' high). Delay", "the smoke cloud takes 2D6 rounds to reach its maximum area. A fire of at least 3' in diameter is necessary for this spell."), new Pair("Range", "20' +5' x lvl to source of fire."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
