			mSpellDescriptions.add(new SpellDescriptionRecord("Force VI", "As \"Force I\", but does 13D6 to one opponent and 7D6 to all creatures in a 20' burst radius.", new Pair("Range", "10' x lvl"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
