			mSpellDescriptions.add(new SpellDescriptionRecord("Muteness", "This spell prevents its victim from speaking. A save Vs magic is allowed (of course). A magician may burn a few mana on a silent \"Dispel Magic\" (if they are capable of it), but the success rate is 1/2 that of a normal success.", new Pair("Range", "10' x level."), new Pair("Duration", "A save Vs stress is allowed every 6D6 hours until the victim successfully saves."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
