			mSpellDescriptions.add(new SpellDescriptionRecord("Protection IV", "As \"Protection I\", but minimum Armor for nonmagical weapons 100%. Minimum Armor for silver and magic is 70%.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection V", "As \"Protection I\", but minimum Armor for nonmagical or silver weapons is 110% (note that silver weapons are grouped with non -magical ones). Minimum Armor Rating for magical attacks is 80%.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection VI", "As \"Protection I\", but minimum Armor for nonmagical or silver weapons is 125%. Minimum Armor for magical weapons is 90%.", new Pair("Range", "touch."), new Pair("Duration", "1 hour x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection VII", "As \"Protection I\", but minimum Armor for non-magical weapons or silver is 150%. Minimum Armor for magical weapons is 100%.", new Pair("Range", "touch."), new Pair("Duration", "2 hours x level. (All evil religions)- Accompanied by a Human Sacrifice performed at a Black Mass, its Duration is forever."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection VIII", "As \"Protection VII\", but magical weapons of a non-HOLY nature are grouped with silver and nonmagical weapons.", new Pair("Range", "touch."), new Pair("Duration", "2 hours x level. (All evil religions)- Accompanied by a Human Sacrifice performed at a Black Mass, its Duration is forever."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection Aura", "This spell is cast over any article of clothing, including armor, giving it a faint glow visible only in total darkness. It causes the item to become enchanted to +l protection (+5% to Armor and +l to Absorb when hit in that location). This particular enchantment also adds +10% to all saves made against creatures that are nocturnal in nature (including undead).", new Pair("Range", "touch."), new Pair("Duration", "1 week (may be made permanent if the mage invests 2 mana pts into the object PERMANENTLY)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Animals", "This spell creates a 10' radius sphere that prevents animals from entering. However, any solid object or magical force that penetrates the sphere from the inside will destroy it.", new Pair("Range", "surrounds mage."), new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Charm", "This spell gives the recipient a 50% resistance to \"Hypnotism\" and similar spells (if he FAILS to resist he is STILL allowed a saving throw).", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Cold", "This spell totally protects the recipient from the effects of non -magical cold and cold spells cast by the mage casting this spell. It will totally protect the recipient from the effects of magical cold created by outside forces for 1D3+3 rounds and will subtract -1 damage from each die of magical cold damage that occurs after that (for the length of the spell).", new Pair("Range", "touch."), new Pair("Duration", "30 minutes."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Dark", "This spell protects the recipient from attacks made by nocturnal creatures (including supernatural ones) subtracting 10% from their attacks and adding +10% to any saves the recipient must make Vs their attacks.", new Pair("Range", "touch."), new Pair("Duration", "1 hour +10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Demons", "This spell creates a sphere of protection that surrounds the casting mage, preventing any demon of level less than or equal to that of the mage from passing through. Demons of greater level than that of the mage must save Vs magic -10% to pass through. Until the mage reaches 7th level he must paint a magical circle upon the ground around him (the sphere doesn't move). 7th levels and up merely chant and form an invisible circle around themselves. The invisible circle may move \"6\".", new Pair("Range", "about the mage."), new Pair("Duration", "10 minutes x level. Sphere has a radius of 10'."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Fear", "This spell surrounds the mage with a sphere of 10' radius that totally stops all \"Fear\" and \"Fear\" type spells from affecting creatures inside. It may be cast upon another creature (by touch) but it won't have the radius of effect then.", new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Fire", "This spell totally protects the recipient from the effects of non -magical fire and from fire spells cast by the mage casting this spell. It will totally protect the recipient from the effects of magical fire cast by outside forces for 1D3+3 rounds and Will subtract -1 damage from each die of magical fire damage that occurs after that (for the length of the spell).", new Pair("Range", "touch."), new Pair("Duration", "30 minutes."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Fire : Item", "This spell is specifically designed to protect precious items that people might be afraid could be destroyed in a fire. See \" Protection : Fire \" for the exact stats.", new Pair("Range", "touch."), new Pair("Duration", "30 minutes."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Lightning", "This spell totally protects the recipient from the effects of any form of electricity for 10 minutes x level of mage or until the recipient has been protected from a number of dice equal to the level or the mage (90 if the mage is 10th level, it will protect the recipient from 10 dice of damage before ending).", new Pair("Range", "touch."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Lycanthrope", "This spell is exactly like the spell \"Protection: Demons\" (above) but protects the mage from Lycanthropes.")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Magic", "This spell totally protects its recipient from the effects of \"Hypnotism\" and similar spells, It adds +15% to the character's save Vs magic and deducts 3 points per die of magic damage done.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level or protection from 100 mana points."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Missiles", "This spell causes missile damage to score off the character's Stamina first (instead of Hits). If cast on a Warrior or Thief that successfully \"Dodged\" Vs a missile attack, then the damage is not scored at all.", new Pair("Range", "touch."), new Pair("Duration", "2D6 + level rounds."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Sleep", "Gives the recipient a 50% resistance to \"Sleep\" spells, failing that he still get a save sometimes.", new Pair("Range", "touch,"), new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Teleport", "Causes all \"Teleport\" type spells to have a +50% chance failure and \"Safe Teleport\" to have a +25% chance failure. Costs 1 permanent mana for the mage to cast.", new Pair("Area of Effect", "100' x 100' x 100' area."), new Pair("Range", "must be placed on a gem worth minutes of 5000 sp."), new Pair("Duration", "forever!"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Traps", "This spell prevents traps from triggering on its recipient (does NOT give immunity from trap effects).", new Pair("Range", "touch."), new Pair("Duration", "6 traps or 30 minutes."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Protection : Undead", "This spell is identical to \"Protection: Demons\" except that it works Vs undead.")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Quest", "As the spell \"Geas\", except that failing to carry out the demands of the casting mage causes the spell victim to lose -1% from his saving throws each day (all saves). The saves will continue to drop -1% each day until the quest is fulfilled. This spell can only be negated by the casting mage and does not die when he does. The Quest Failure Table Roll 1D100 01-30	Recipient takes 01-100 points of damage and if he lives then he did not fail. 31-40	Recipient takes 6D6 damage (save vs. magic allowed for 1/2 damage). 41-45	Recipient loses 1D6 levels from a \"Leveloss\" spell. 46-60	Recipient loses 1D3 levels from a \"Leveloss\" 61-65	Recipient saves Vs stress or goes insane forever. 66-75	Recipient saves Vs stress or goes insane for 1D100 days. 76-95	Recipient succeeds but must save Vs shock or lose 1 point of WP permanently. 96-00	Roll Twice (taking both effects) The maximum allowed percentage for Success should be 90%")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Raise Army", "This spell allows the casting mage to be able to raise and train men from the surrounding populated areas to serve in a religious endeavor (they will serve at 1/2 the normal costs in wages, upkeep of equipment, etc.). The mage may influence 50 men x his level in this way. A 10th level mage could influence up to 500 men to serve him for 1/2 the normal costs.", new Pair("Area of Effect", "5 miles x level."), new Pair("Duration", "1 week (after which the spell must be recast)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rally", "This spell instills bravery in the hearts of men, allowing the caster to negate the effects of fear and broken morale and rally up to 10 men x level around him. Men so rallied will ignore future calls to check morale and right to the death so long as the casting mage fights with them. Individuals (players) that wish to save Vs this enchantment are allowed to do so, but must do so when the enchantment is cast (not ten minutes later in the fight) and risk being branded as cowards or traitors if they choose to flee.", new Pair("Area of Effect", "a sphere of radius 20' x level (surrounding the mage)."), new Pair("Duration", "15 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Read", "This spell gives the caster a ( 25% +5% per level chance ) to read any language.", new Pair("Range", "Caster ONLY"), new Pair("Duration", "12 Hours"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Realm of Darkness", "This spell creates a 400' diameter sphere of evil darkness. It acts just like \"Perpetual Darkness\" with the following exceptions: 1) 	Characters with morals of +5 or greater will have -5% modifiers applied to their Hit Bonus, Missile Bonus and Bow Bonus (not to mention their Defense and Armor Rating. 2) 	Creatures with morals of -4 to +4 must save Vs stress to enter into the dome and take 1D6 and be driven back if they fail (a \"Hide Location\" spell prevents this). This spell is stationary and MUST be cast on ground deemed \"holy\" to the church of Rashok (i.e. no fair casting it in city streets).", new Pair("Duration", "one week (or permanent if 4 permanent mana points are cross off). The spell must be cast upon a key religious object within the church."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Recruitment", "This spell allows the casting mage to go out into the streets of any basically \"good\" city and find willing warriors for the church of Thanatos. These Warriors will only serve the mage if his quest if religiously oriented. This spell will bring 1D6 Warriors. Roll 1D6 for each one. 1-2 means the Warrior is 1st level. 3-4 means the Warrior is 2nd level. 5 means the Warrior is 3rd level. 6 means the Warrior is 4th level. If the mage's CHARISMA is \"15+\" he may recruit +l Warrior. If the mage's CHARISMA is 6 or less he recruits -1 Warrior. Level 1 or 2 Warriors will be \"average\" Warriors, but level 3 or 4 Warriors will be \"superior\" Warriors. This spell may only be cast 1 time per month and a maximum number of Warriors equal to the level Or the casting mage may be maintained. Support and upkeep is up to the casting mage.", new Pair("Delay", "1 day."), new Pair("Duration", "1 week (after which this spell must be cast again)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Reflection", "This spell surrounds the caster with an \"Image\" that resembles the creature that looks at him (must be approximately humanoid size), causing it to believe that it sees itself instead of the casting mage. The victim of this spell must save Vs magic in order to attack the caster (after which the spell is broken).", new Pair("Duration", "1 round x level +1D6 rounds."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Regeneration", "This spell will return Hit Points at rate 1 per 2 minutes or Stamina at 1 per minute. It will restore lost limbs at \"1 limb\" per 150 mana spent and lost eyes at \"1 eye\" per 300 mana spent (mana may be spent over days of time). Any wounds incurred while or before this spell is in effect will be cured, except those caused by Fire or Acid. It is possible to regenerate back to life. but only if the spell was thrown before the recipient died.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Air I", "This spell removes the air around 1 creature. causing it to save Vs unconsciousness or faint for 2D10 rounds. There is a 60% chance it will also snuff the torch the creature is carrying.", new Pair("Range", "10' x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Air II", "As \"Remove Air I\", but affects a 10' x 10' area.", new Pair("Range", "10' x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Air III", "As \"Remove Air I\", but works over a 30' x 30' area.", new Pair("Range", "10' x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Air IV", "As \"Remove Air I\", but the save is -10% and torches are snuffed 75% of the time.", new Pair("Area of Effect", "60' x 60'."), new Pair("Range", "10' x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Curse", "This incantation removes, dispels or banishes any curse of any sort including delusions, insanity (G.M. discretion), etc. It does NOT cure \"Possession\", which must be cured by an \"Exorcism\".", new Pair("Success", "35% +5% per level of mage. May be attempted only once."), new Pair("Range", "30' (A \"Curse\" spell (Arcane Lore) must be faced and commanded to leave this plane)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Earth", "This spell will remove 200 cu.ft of stone, 1000 cu.ft. of earth or 2000 cu.ft. of sand. The removed material will pile up on each side of the hole made by this spell (at 11th level the mage may choose to have it \"Disintegrate\" at no extra cost). It does not affect magical substances (like \"Wall of Stone\"). This spell takes 2D6 rounds to take effect.", new Pair("Range", "20' +2' x level. If cast on walls there is a 50% chance (G.M.'s discretion) that part of the roof will collapse."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Remove Scar", "This spell will remove most scar's, if for some reason it is a very large scar, sometimes it will require several castings.  If we are talking total disfigurement, then it will require the use of 1D3 Permanent Mana Points to  remove the scar tissue and repair the Damage.", new Pair("Range", "Touch"), new Pair("Duration", "Permanent"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Restore", "This spell is a process as much as a spell, by which the caster can restore completely ancient books/scrolls and works of art to their original state.  The piece must be complete, it cannot be missing pieces otherwise the spell and work will be for nothing.", new Pair("Range", "Caster ONLY"), new Pair("Duration", "30 Days"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Resurrection", "This spell sends the recipient to the shadow lands to search for the soul of a dead friend or ally. If the mage desires to go he may do so, but usually he will send a substitute. If the recipient of this spell is successful in his quest, the body is returned to lire. If the recipient fails, he must roll on the Quest Failure Table below:", new Pair("Success", "10% x level of recipient (-5% x number of days the deceased person has been deceased)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Return", "If the mage dies with this spell in effect he will return to the living within 1D10 days (his body vanishes from its resting place).  He will have +1 on all stats. This spell may be cast as often as the mage desires, but it is only USABLE once per level (i.e. once at 13th, once at 14th etc.).", new Pair("Duration", "24 hours (one day and one night). These mana points do not return until the spell has expired."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Ritual Sacrifice", "This spell requires a very private setting, where screams will not be noticed.  The caster first takes the victim into a bed chamber where they very tenderly have sex, then the victim is drugged, strung up, spelled and Vivisected ALIVE.  This spell also gains 16% to the prayers of the casting priest of Narese.  These bonus's are NOT cumlative.", new Pair("Range", "Touch"), new Pair("Duration", "Level X Days"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Ritual Torture", "This spell is used to curry Favor from Narese, it please her to see a skillfull torture keep their victims alive, long after they want to die.  If the character has completed this ritual once in their last level and they call upon Narese, she is 16% more likely to aide them in they're request.  This spell helps to keep victims alive long after they really should have died, don't get too drastic with this, if you cut someone's head off, they Die.", new Pair("Range", "Touch"), new Pair("Duration", "Level X Days"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rule Undead", "This spell is an improved \"Control Undead\" spell, allowing the caster to add +25% to his Success Rate, and allowing him to control up to twice his level in undead.")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Combat I", "After painting or etching this mystic rune on a weapon it becomes enchanted to +l (whenever the mage expends 1 mana point on it). The weapon will add +5% to its wielder's Hit Bonus and +l to his Damage Bonus. The mage may (of course) choose to permanently embed 1 mana point into the sword (after he's had this spell for 4 levels), making the sword (or other weapon) into a continuously functioning one.", new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Combat II", "As \"Rune of Combat I\", but this rune is placed on a +l enchanted weapon, changing the weapon to +2 enchantment. It has NO EFFECT on non-magical weapons. The mage must invest 3 more mana points into this weapon to make it continuously function (as per the rules on making magic items).", new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Combat III", "This one makes the weapon +3 enchantment (+15 Hit Bonus, +3 Damage) and it must be cast upon a weapon having a \"Rune of Combat I\" and a \"Rune of Combat II\" on it. It is made continuously functioning by investing mana into it permanently.", new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Combat IV", "As \"Rune of Combat III\" (sigh), but relies upon the weapon containing \"Runes of Combat I-III\", and enchants the weapon to +4 (+20% Hit Bonus, +4 Damage). Made permanent with the loss of mana.", new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Combat V", "As \"Rune of Combat IV\", but needs \"Runes of Combat I-IV\" in order to function. Enchants the weapon to +5 (+25%.+5 dam). This rune may only be cast upon weapons that are highly moralistic in nature or highly unmoralistic. May be made continuously functioning by investing permanent mana into the weapon (192 mana).", new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune : Slay Lycanthropes", "This rune will cost the mage 32 mana points to make (and place on a weapon). After that, whenever that weapon hits a lycanthrope (for Hit Points damage) it will do 1D100 points to it (NST). The inks for this rune are very expensive, costing 2D6 x 250 sp to buy.")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune : Slay Undead", "This rune will cost the mage 64 mana points. After that, whenever the weapon hits an undead having 10 or less levels, it will do 1D100 points to it. If the undead has 11 or more levels, it will do 1D100 damage ONLY if the undead fails a save Vs magic. The inks for this rune need dried mummy's blood (cost is 4D6 x 250 sp in town).")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Protection : Animals", "This symbol may be drawn in the dirt (or whatever) and costs NO MANA (or Stamina) to cast. It will cause any animals of a wild nature that are under the influence of magic to flee in terror. Animals that are acting under their own influence (i.e. not controlled) will flee only if they fail to save Vs magic. Non -mages with an IN+WS of \"20+\" may learn to draw this rune when they reach 8th level (if taught by a Druid). There will only be 75% successful with it. NOTE: Misuse of this rune will supposedly anger the gods of Nature (according to rumors) and will DEFINITELY anger any Druid that sees it painted on your shield.", new Pair("Duration", "6 months."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Rune of Protection : Magic", "This rune functions like a continuously working \"Protection:Magic\" spell. The inks to paint it cost 3D6 x 200 sp.", new Pair("Duration", "3 months or protection from 100 mana points worth of spells."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Safe Teleport", "This spell acts just like \"Teleport II\", but costs more. It guarantees that if the mage knows where he is and where he's desiring to go that he makes it without failure. It does NOT guarantee safe teleport to Wizards' castles, etc. (which are usually protected by various spells to prevent this).")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Salve of Healing", "This spell allows the mage to create a special ointment that will heal wounds more rapidly and fight off infection. Wounds treated with \"Salve of Healing\" will heal at rate 1 point per hour. The more capable the magician is that manufactures the salve, the longer the salve will heal the wound (and therefore the greater the number of Hits will be healed). The salve will last in effectiveness for 1 hour per 3 levels of experience of the mage that manufactured it. It must be kept away from air, as it will rapidly diminish in effectiveness. Even kept in an air-tight", new Pair("location it will go \"bad\" in around 2 weeks. Cost", "the mage must either spend 1000 silver pieces in town or one week in the woods in order to obtain the necessary ingredients to make this salve. One \"batch\" equals 10 \"doses. The salve may only be applied once per week to any wound. When combined with an \"Anoint\" spell this salve gains in effectiveness."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sand Bolt", "With this spell the mage shoots a bolt of spraying sand at his opponents. From 1D6 human-sized opponents to 1 large creature may be affected. The mage rolls an attack adding Missile Bonus", new Pair("(attempting to hit Armor", "60%). A \"hit\" scores 1D6+1 points of damage and knocks the opponent backward (roll on \"CET\" adding +10%). Opponents that fail to save Vs magic are blinded for 1D6 rounds."), new Pair("Range", "30'."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Scattered Showers", "This spell is an extremely limited \"Control Weather\" spell that has a 15% chance x level of causing a small downpour (HALVE the chances if cast in a dry or desert region). This spell may only be cast once per week by the same mage.", new Pair("Area of Effect", "1/2 mile x level radius)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Seance", "This spell allows the mage to contact the spiritual plane and speak to the dead without needing the body as a prop. The desired dead person may not have been dead for more than one week +l week per level past 7th. The casting mage may ask the spirit three questions. The spirit may say whatever he wishes, but the mage will be able to tell the truthful statements from the false ones as though he had a \"Detect Lie\" spell cast on him.", new Pair("Range", "???"), new Pair("Duration", "takes 1D3 x 10 minutes to perform."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sea Safety", "This spell is cast upon a ship. It will repel sea monsters, mermen", new Pair("and other non-natural encounters at sea. Success", "50% + 5% per level past 9th."), new Pair("Duration", "one week x level of mage (or permanent if the casting mage permanently invests 4 mana into this spell). This spell may also be used to dispel the WHIRLPOOL created by a water elemental. Success is the same as above."), new Pair("Range", "10' x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
			mSpellDescriptions.add(new SpellDescriptionRecord("Secret Door", "Creates a doorway through any NON-Magical substance, 36\" wide, 72\" high and up to 24\" deep.  The spell can be permanent with the expenditure of those Permanent Mana Points.", new Pair("Range", "Touch"), new Pair("Duration", "1 Minute X Level"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Secret Room", "This spell creates a 12' X 12' X 7' room out of extra-dimensional space.   This spell requires the use of (5) Permanent Mana points to create and maintain your room.  Upon death the contents will be lost unless the caster spends an additional (2) Permanent Mana points to have all of their stuff sent to whatever destination was desired by the owner.  This makes the total Permanent Mana cost id (7).")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("See Invisibility", "Allows the mage to see invisible objects up to 10' x level away.", new Pair("Success", "50% + 15% per level."), new Pair("Duration", "10 minutes` x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Animation", "This spell functions similarly to the Necromancy spell \"Animation\". It allows the mage to lock a \"shadow spirit\" within a dead body, causing it to be animated for 1 day. It will follow his commands to the letter (without requiring a \"Control Undead\" type spell and may NOT be stolen by a Necromancer's commands. The mage does NOT need to \"invest\" the points into his animated creature, and at the end of the day the animated form reverts back to a lifeless corpse (after 24 hours). The animated dead can speak, but it's voice will be eerie and its actions will be zombie -like. The body must be freshly dead (not more than 2D6 hours). It may not be animated more than once. It will have its old statistics, but may NOT cast spells.", new Pair("Range", "5' x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Climb", "This spell allows the casting mage to form a ladder out of shadows. He may list the persons that will be able to touch the ladder, and to all others the ladder will be without substance. The mage must be the first one to climb up the ladder (in order to fashion it), an he must be the last one to climb down (it is destroyed as he climbs down it). Persons on the ladder are affected with a \"Blend with Shadows\" spell.", new Pair("Range", "50'."), new Pair("Duration", "10 minutes x level or as above. This spell is destroyed by very bright light."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Cure", "This spell heals Hit Points damage (one per day plus the normal one per day).", new Pair("Range", "touch."), new Pair("Duration", "1 day x level. Any other cure spell cast while this spell is in effect will cancel it."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Eye I", "This spell functions exactly like the Arcane Lore spell \"Farsee\", except its range is only 1000' and instead of gazing in a reflective surface the mage gazes into shadows. This spell does NOT require a focus.")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Eye II", "As \"Shadow Eye I\", but range is as \"Farsee\".")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Hold", "similar to \"Binding Spells\" (in Control), this spell engulfs its victims in a huge shadow. If the victims are surprised, or below 5th level, there is no save. If they are NOT surprised (and 5th level or above), they are allowed a save Vs magic. If they fail to resist the spell they are held motionless for 2D10 minutes. One victim may be affected by this spell for every odd level the mage possess beyond 5th (so a 10th level mage could hold up to 3 persons).", new Pair("Duration", "as for \"Binding Spell\" (if they break free), or as above."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Images", "This spell is similar to the \"Multiple Image\" illusion. It creates several shadowy images of the caster and makes him appear shadowy as well. These flickering images have a 50% chance of causing any single attack to miss (NST). This spell will not work properly in daylight.", new Pair("Duration", "1D6 +1 rounds per level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shadow Warning", "The mage casts this spell on a portal and when it is opened a telepathic signal is sent out to warn him. The signal has a range of 500 feet. One doorway may be so magicked for each level the mage has (so a 5th level could have 5 magic doors).", new Pair("Range", "touch."), new Pair("Duration", "one year."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sheath", "This spell allows the mage to place his weapon in an extra -dimensional sheath that will follow him wherever he goes. When the mage wishes to draw his weapon, it \"pops\" into his hands (delaying him only \"-l\" instead of \"-5\" The \"Sheath\" spell", new Pair("may hold any weapon up to WL", "9. The priest of Exonerous that uses this spell will consider it a Perversion of the will of his god to use this spell for \"other\" purposes than carrying a weapon (such as for carrying gold, etc.), however this rule might be bent for a very religious cause. The age may have only ONE \"Sheath\" spell functioning at any one time."), new Pair("Range", "easy reach."), new Pair("Duration", "until mage dies (the item is permanently lost if inside the \"Sheath\")."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shell of Water Breathing", "Any air-breathing creature within the area of effect will be able to breath water as if it were air. This spell must be cast on some \"central\" item in order to focus its power effectively.", new Pair("Area of Effect", "sphere of radius 10' x level."), new Pair("Duration", "one day (if mage invests 4 permanent mana points into it, it will last until his death)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shield I", "This spell must be cast upon an item that is worn (whether it be jewelry, clothing, armor, etc.). If it is cast on armor it will add +5% to the wearer's Armor and Belief Ratings and deduct 1 point from Hit Points damage (when hit in that location). If the spell is cast on any other item it will add +5% to the wearer's Armor Rating, Belief Rating and Saving Throws (except surprise). Note: any of the \"Shield\" spells may be cast on articles other than wearable ones, but please don't make it ridiculous (a \"thimble of protection\"?). This spell is NOT cumulative with other spells of protection (like \"Shield II-V\" for example).", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level (or permanent if the mage permanently invests the mana point cost in the object (provided it follows the on \"Making Magic Items\" on page 119))."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shield II", "As \"Shield I\", but adds 10% instead of 5%.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level (or permanent if the mage permanently invests the mana point cost in the object (provided it follows the on \"Making Magic Items\" on page 119))."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shield III", "As \"Shield I\", but adds 15% instead of 5%.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level (or permanent if the mage permanently invests the mana point cost in the object (provided it follows the on \"Making Magic Items\" on page 119))."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shield IV", "As \"Shield I\", but adds 20% instead of 5%.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level (or permanent if the mage permanently invests the mana point cost in the object (provided it follows the on \"Making Magic Items\" on page 119))."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shield V", "As \"Shield I\", but adds 25% instead of 5%.", new Pair("Range", "touch."), new Pair("Duration", "10 minutes x level (or permanent if the mage permanently invests the mana point cost in the object (provided it follows the on \"Making Magic Items\" on page 119))."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Shield of Light", "This spell surrounds the caster with a 10' radius dome of light that keeps out ALL undead with less than 4 levels (or hit dice). Undead with 4 or greater levels are allowed a save Vs magic. Affected undead will either flee (a roll of \"1-4\" on a D6) or cower where they are (outside the dome). Greater Wraiths and above will NEVER cower (they'll just split)", new Pair("Duration", "one hour."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Silence", "This spelL allows the recipient totally silent movement for two hours.", new Pair("Range", "touch."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Simple Sounds", "This spell creates the illusion of blurred speech, growls, groans,", new Pair("rattling chains, etc. Volume", "up to that of about 3 people talking. The mage may make this sound appear to originate from any specific point of origin within his spell range."), new Pair("Range", "10' x level."), new Pair("Duration", "1 minute (4 rounds) x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sink", "This spell causes the earth below the spell's victim to open up and swallow him. This spell must be cast through a touch of some kind (such as through the mage's weapon, staff or hand).", new Pair("Range", "touch."), new Pair("Duration", "1 round x level or until the mage touches somebody."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sleep I", "This spell causes one subject to fall asleep. Creatures below 5th level don't get a save. This spell doesn't affect creatures above 8th level.", new Pair("Duration", "3D6 x 10 minutes."), new Pair("Range", "ten feet (that's right!)."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sleep II", "As \"Sleep I\" except affects a 30' x 30' x 30' area. Puts to sleep up to 2D6 level one or two creatures, 1D6 level three or four creatures (all without saves). It also affects one creature of up to 8th level (save allowed).", new Pair("Range", "120' to edge."), new Pair("Duration", "3D6 x 10 minutes."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sleep Eternal", "The victim of this spell rolls a saving throw Vs magic -10%. If he fails his save he falls into an eternal slumber that ONLY the mage can reverse (with the exception of a \"Wake\" spell).", new Pair("Range", "10' x level."), new Pair("Duration", "???"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Sleight of Hand", "This enchantment allows the mage to perform parlor magic tricks, cheat with some skill at cards or pocket something undetected. It requires NO chanting and NO gesturing (merely", new Pair("concentration). Success", "30% + 3% x DX. Subtract 2% for each observer. This spell will not work under close scrutiny (i.e. if the mage is suspected of this spell)."), new Pair("Range", "none."), new Pair("Duration", "10 minutes."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
			mSpellDescriptions.add(new SpellDescriptionRecord("Slight Odors", "Allows the mage to simulate or conceal slight odors (such as flowers, weak poisons (type 10 or less etc.).", new Pair("Range", "10' x level."), new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Slight Tastes", "Allows the mage to simulate or conceal the essence of slight tastes such as normal meals, weak poisons (up to type 10).", new Pair("Range", "10' x level to point of origin."), new Pair("Duration", "1 minute x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Slow", "This spell increases the gravitational pull on any one object. On humanoids it increases their encumbrance by 50 lbs + 10 lbs x level (save allowed for 1/2  effect). On non-humanoids (or when carrying capacity is not being used) it slows them to 1/2 movement and subtracts 5% from combat and 1 from Speed. This spell should not affect large creatures very much (like elephants and dragons).", new Pair("Range", "50'."), new Pair("Duration", "1D6 + level in rounds."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Smoke Servant", "Made from the wisps of a smoky campfire, this being has 2D6+6", new Pair("Hits, and does 1D6+2 damage (Hit Bonus - +25%, ASP", "+1 WL like the mage that summoned them. A \"Smoke Servant\" may only be harmed with magical attacks. They will continue to exist for as long as the campfire burns. The mage may have a number of \"Smoke Servants\" under his command equal to 1/3 his level, but he may not use the mana points he expended in conjuring them until they are \"extinguished\"."), new Pair("Range", "The mage must be within 40' of the fire to conjure the servant, and then it has an effective range of 1/2 mile times the mage's level."), new Pair("Duration", "until the campfire is extinguished."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$ //$NON-NLS-7$ //$NON-NLS-8$
			mSpellDescriptions.add(new SpellDescriptionRecord("Snatch", "This spell is a limited one-shot telekinesis spell that's used by the mage to \"grab\" something from some where and pull it to him. The object may not be more than 1 cu.ft. in volume and may not weigh more than 50 lbs. If it is on the person of someone, that someone is allowed to save Vs magic-10%. If that someone is holding the item to be snatched, he is allowed to save unmodified. (This is the spell that Vader used on Solo in George Lucas's The Empire Strikes Back. Solo failed his save).", new Pair("Range", "5' x level."), new Pair("Duration", "1 round."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Soul Store", "This spell allows the casting mage to enchant an object to act as a house for his soul. The mage may then (at any time) detach his soul from his corporeal body and place it within that object (his soul automatically goes there if the mage is ever killed). If the mage so desires he may attempt to \"possess\" any other living creature within 30' of the object his soul is in. The rules on possession are found elsewhere in this book. Only one object may be enchanted in this way by the mage at any one time. If the object is destroyed in any way other than a \"Dispel Magic\", the mage's immortal soul is snuffed (the mage is permanently dead).", new Pair("Range", "touch (when first enchanting it). The mage must have the item within 60' of him when placing his soul into it."), new Pair("Duration", "forever."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Speak with  Animals", "This spell allows the mage to speak telepathically with any one type of animal. Animals spoken to will generally have a favorable reaction to the mage and his party. They will usually not attack unless attacked (referee discretion, of course).", new Pair("Range", "hearing."), new Pair("Duration", "1 hour x level."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Speak with  Dead", "This spell is very similar to \"Seance\", but requires the dead body to be before the casting mage.", new Pair("Range", "10'."))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Speak with  Stones", "This spell allows the magician to communicate with stones (or other rock substances of a non refined nature). The magician will know what has passed by the stone within the last 1 hour x level. Includes any conversations that transpired, etc.", new Pair("Range", "touch."), new Pair("Duration", "5 minutes (but only one rock may be communicated with)"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
			mSpellDescriptions.add(new SpellDescriptionRecord("Spears of Light I", "This spell causes 1D6 spears of burning light to burst from the mage's fingertips, striking his victim for 1D6+1 damage (each). Only one creature may be affected by this spell each round.", new Pair("Range", "5'"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$
			mSpellDescriptions.add(new SpellDescriptionRecord("Spears of Light II", "As \"Spears of Light I\", but the victim of this spell must save Vs magic -20% or be permanently blinded (unless magically healed).")); //$NON-NLS-1$ //$NON-NLS-2$
			mSpellDescriptions.add(new SpellDescriptionRecord("Speed I", "This enchantment adds +l to the recipient's Speed with any weapon or spell. A character that is subjected to more than one \"Speed\" spell per week must roll less than his CN or lose 1 permanent point of", new Pair("Range", "touch."), new Pair("Duration", "1 minute (4 rounds)"))); //$NON-NLS-1$ //$NON-NLS-2$ //$NON-NLS-3$ //$NON-NLS-4$ //$NON-NLS-5$ //$NON-NLS-6$
